/*Table structure for table `pair_score` */

DROP TABLE IF EXISTS `pair_score`;

CREATE TABLE `pair_score` (
  `sys_id` int(11) DEFAULT NULL,
  `user1_id` int(11) DEFAULT NULL COMMENT '结对队友1ID',
  `user2_id` int(11) DEFAULT NULL COMMENT '结对队友2ID',
  `task_id` int(11) DEFAULT NULL COMMENT '所属作业ID',
  `sys_score` double DEFAULT NULL COMMENT '本对本次作业总得分'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `pair_score` */

/*Table structure for table `pair_score_details` */

DROP TABLE IF EXISTS `pair_score_details`;

CREATE TABLE `pair_score_details` (
  `sys_id` int(11) NOT NULL AUTO_INCREMENT,
  `pair_score_id` int(11) DEFAULT NULL COMMENT '结对成绩的ID',
  `details_id` int(11) DEFAULT NULL COMMENT '评分细则项ID',
  `score` int(11) DEFAULT NULL COMMENT '该项得分',
  PRIMARY KEY (`sys_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `pair_score_details` */

/*Table structure for table `performance` */

